        this._detailService = _detailService;
        this.initPlugin = false;
    }
    async ngAfterViewInit() {
        const showAlert = async (message) => {
            await Dialog.alert({
                title: 'Error Dialog',
                message: message,
            });
        };
        try {
            await this.runTest();
            document.querySelector('.sql-allsuccess').classList
                .remove('display');
            console.log("$$$ runTest was successful");
        }
        catch (err) {
            document.querySelector('.sql-allfailure').classList
                .remove('display');
            console.log(`$$$ runTest failed ${err.message}`);
            await showAlert(err.message);
        }
    }
    async runTest() {
        try {
            let result = await this._sqlite.echo("Hello World");
            // ************************************************
            // Import Json Object Issue#59
            // ************************************************
            // test Json object validity
            result = await this._sqlite
                .isJsonValid(JSON.stringify(dataToImport59));
            if (!result.result) {
                return Promise.reject(new Error("IsJson failed"));
            }
            // full import
            result = await this._sqlite
                .importFromJson(JSON.stringify(dataToImport59));
            if (result.changes.changes === -1)
                return Promise.reject(new Error("ImportFromJson 'full' failed"));
            ;
            // ************************************************
            // Export Json Object from an Existing Database
            // ************************************************
            // create the connection to the database
            const db = await this._sqlite
                .createConnection("db-from-json59", false, "no-encryption", 1);
            if (db === null)
                return Promise.reject(new Error("CreateConnection db-from-json59 failed"));
            // open db testNew
            await db.open();
            // create synchronization table 
            result = await db.createSyncTable();
            if (result.changes.changes < 0)
                return Promise.reject(new Error("CreateSyncTable failed"));
            result = await db.getSyncDate();
            if (result.length === 0)
                return Promise.reject(new Error("GetSyncDate failed"));
            // export json
            let jsonObj = await db.exportToJson('full');
            // test Json object validity
            result = await this._sqlite
                .isJsonValid(JSON.stringify(jsonObj.export));
            if (!result.result) {
                return Promise.reject(new Error("IsJsonValid export 'full' failed"));
            }
            // close the connection
            await this._sqlite.closeConnection("db-from-json59");
            return Promise.resolve();
        }
        catch (err) {
            // close the connection
            await this._sqlite.closeConnection("db-from-json59");
            return Promise.reject(err);
        }
    }
}
