export async function deleteDatabase(db) {
    try {
        let ret = await db.isExists();
        if (ret.result) {
            const dbName = db.getConnectionDBName();
            await db.delete();
            return Promise.resolve();
        }
        else {
            return Promise.resolve();
        }
    }
    catch (err) {
        return Promise.reject(err);
    }
}
